={{ (() => {
  const maxLen = 280;
  let raw = ($json.output ?? '').toString();
  raw = raw.replace(/\r\n/g, '\n').trim();

  // Corrige "√ìtimo!Tem" -> "√ìtimo! Tem" (e similares)
  raw = raw.replace(/([.!?])([^\s\n])/g, '$1 $2');

  // 1) Respeita \n\n (formato do agente). Se n√£o tiver, tenta \n. Se n√£o tiver, usa o texto todo.
  let parts;
  if (raw.includes('\n\n')) parts = raw.split(/\n{2,}/);
  else if (raw.includes('\n')) parts = raw.split(/\n+/);
  else parts = [raw];

  parts = parts.map(p => p.trim()).filter(Boolean);

  const splitSentences = (text) => text
    .replace(/([.!?])\s+/g, '$1|')
    .split('|')
    .map(t => t.trim())
    .filter(Boolean);

  const chunkByWords = (text) => {
    const words = text.split(/\s+/).filter(Boolean);
    const out = [];
    let buf = '';
    for (const w of words) {
      const next = buf ? `${buf} ${w}` : w;
      if (next.length > maxLen) {
        if (buf) out.push(buf);
        if (w.length > maxLen) {
          for (let i = 0; i < w.length; i += maxLen) out.push(w.slice(i, i + maxLen));
          buf = '';
        } else {
          buf = w;
        }
      } else {
        buf = next;
      }
    }
    if (buf) out.push(buf);
    return out;
  };

  const out = [];

  const pushChunked = (text) => {
    if (!text) return;
    if (text.length <= maxLen) {
      out.push(text);
      return;
    }

    const sentences = splitSentences(text);
    if (sentences.length <= 1) {
      chunkByWords(text).forEach(x => out.push(x));
      return;
    }

    let buf = '';
    for (const s of sentences) {
      const next = buf ? `${buf} ${s}` : s;
      if (next.length > maxLen) {
        if (buf) out.push(buf);
        if (s.length > maxLen) chunkByWords(s).forEach(x => out.push(x));
        else buf = s;
      } else {
        buf = next;
      }
    }
    if (buf) out.push(buf);
  };

  // Se veio tudo em 1 bloco grande, tenta quebrar por frases (melhora consist√™ncia)
  if (parts.length === 1 && parts[0].length > 80) {
    pushChunked(parts[0]);
  } else {
    parts.forEach(p => pushChunked(p));
  }

  return out.map(x => x.trim()).filter(Boolean);
})() }}

